import logo from './logo.svg';
import './App.css';
import MainView from './components/main-view/main-view';
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import { useState } from 'react';
import Register from './components/register-view/register';


function App() {
  const [menuItem, setmenuItem] = useState("");

  const Nav = ( <Nav variant="pills" className="justify-content-end" defaultActiveKey="/home">
  <Nav.Item>
    <Nav.Link href="#home">Home</Nav.Link>
  </Nav.Item>
  <Nav.Item>
    <Nav.Link eventKey="#register" onClick={()=>{
      setmenuItem("register")
    }}>Register</Nav.Link>
  </Nav.Item>
  <Nav.Item>
    <Nav.Link eventKey="#login" onClick={()=>{
      setmenuItem("login")
    }}>Login</Nav.Link>
  </Nav.Item>
</Nav>);

if (menuItem == "register")
{
  return (
    <Container>   
      <Nav></Nav>
      <Register />
    </Container> 
       );
}
else if (menuItem == "login")
{
  return (
    <Container>   
      <Nav></Nav>
      <Register />
    </Container> 
       );
}

else {
  return (
    <Container>   
        <MainView />
      </Container> 

       );
}

}

export default App;
